mapper.select:
select

from customer_info
where
1=1

  AND CUSTOMER_NAME LIKE #{condition.customerName}"%"


  AND CUSTOMER_TYPE = #{condition.customerType,jdbcType=VARCHAR}


  AND STATUS = #{condition.status,jdbcType=VARCHAR}


  AND CUSTOMER_CELLPHONE LIKE #{condition.customerCellphone}"%"


  AND CUSTOMER_EMAIL = #{condition.customerEmail,jdbcType=VARCHAR}


  AND CUSTOMER_ADDRESS = #{condition.customerAddress,jdbcType=VARCHAR}


  AND CUSTOMER_REMARK = #{condition.customerRemark,jdbcType=VARCHAR}


  AND CREATED_BY = #{condition.createdBy,jdbcType=VARCHAR}


  AND CREATED_DATE = #{condition.createdDate,jdbcType=TIMESTAMP}


  AND UPDATE_BY = #{condition.updateBy,jdbcType=VARCHAR}


  AND UPDATE_DATE = #{condition.updateDate,jdbcType=TIMESTAMP}


  AND VERSION = #{condition.version,jdbcType=BIGINT}


  AND STATUS = #{condition.status,jdbcType=VARCHAR}

ORDER BY CREATED_DATE DESC
limit #{startIndex},#{limit}

select
count(1)
from customer_info
where
1=1

  AND CUSTOMER_NAME LIKE #{condition.customerName}"%"


  AND CUSTOMER_TYPE = #{condition.customerType,jdbcType=VARCHAR}


  AND STATUS = #{condition.status,jdbcType=VARCHAR}


  AND CUSTOMER_CELLPHONE LIKE #{condition.customerCellphone}"%"


