mapper.select:
select

from stock_list
where 1=1

  AND STOCK_PRODUCT_NAME = #{condition.stockProductName,jdbcType=VARCHAR}


  AND STOCK_PRODUCT_QNO = #{condition.stockProductQno,jdbcType=VARCHAR}


  AND STOCK_PRODUCT_LOCATION = #{condition.stockProductLocation,jdbcType=VARCHAR}


  AND STOCK_PRODUCT_NUM = #{condition.stockProductNum,jdbcType=DECIMAL}


  AND STOCK_PRODUCT_POSITION = #{condition.stockProductPosition,jdbcType=VARCHAR}


  AND STOCK_PRODUCT_PLUS_NUM = #{condition.stockProductPlusNum,jdbcType=DECIMAL}


  AND STOCK_PRODUCT_PLUS_POSITION = #{condition.stockProductPlusPosition,jdbcType=VARCHAR}


  AND STOCK_PRODUCT_FORMAT_NUM = #{condition.stockProductFormatNum,jdbcType=DECIMAL}


  AND STOCK_PRODUCT_GUARANTEE_TIME = #{condition.stockProductGuaranteeTime,jdbcType=VARCHAR}


  AND STOCK_PRODUCT_SINGLE_NETWEIGHT = #{condition.stockProductSingleNetweight,jdbcType=DECIMAL}


  AND STOCK_PRODUCT_SINGLE_CAPACITY = #{condition.stockProductSingleCapacity,jdbcType=DECIMAL}


  AND STOCK_PRODUCT_BATCH = #{condition.stockProductBatch,jdbcType=VARCHAR}


  AND STOCK_PRODUCT_STATUS = #{condition.stockProductStatus,jdbcType=VARCHAR}


  AND STOCK_PRODUCT_STORAGE = #{condition.stockProductStorage,jdbcType=VARCHAR}


  AND STOCK_PRODUCT_WASTAGE = #{condition.stockProductWastage,jdbcType=DECIMAL}


  AND STOCK_PRODUCT_STORAGE_FEE = #{condition.stockProductStorageFee,jdbcType=DECIMAL}


  AND STOCK_PRODUCT_SUPPLIER_SUBJECT_ID = #{condition.stockProductSupplierSubjectId,jdbcType=VARCHAR}


  AND STOCK_MANUFACTURE_DATE = #{condition.stockManufactureDate,jdbcType=DATE}


  AND STOCK_EXPIRE_DATE = #{condition.stockExpireDate,jdbcType=DATE}


  AND CREATED_BY = #{condition.createdBy,jdbcType=VARCHAR}


  AND CREATED_DATE BETWEEN #{condition.startDate} AND #{condition.endDate}


  AND UPDATE_BY = #{condition.updateBy,jdbcType=VARCHAR}


  AND UPDATE_DATE = #{condition.updateDate,jdbcType=TIMESTAMP}


  AND VERSION = #{condition.version,jdbcType=BIGINT}

ORDER BY CREATED_DATE DESC
limit #{startIndex},#{pageSize}

select
count(1)
from stock_list
where 1=1

  AND STOCK_PRODUCT_NAME = #{condition.stockProductName,jdbcType=VARCHAR}


  AND STOCK_PRODUCT_QNO = #{condition.stockProductQno,jdbcType=VARCHAR}


  AND STOCK_PRODUCT_LOCATION = #{condition.stockProductLocation,jdbcType=VARCHAR}


  AND STOCK_PRODUCT_NUM = #{condition.stockProductNum,jdbcType=DECIMAL}


  AND STOCK_PRODUCT_POSITION = #{condition.stockProductPosition,jdbcType=VARCHAR}


  AND STOCK_PRODUCT_PLUS_NUM = #{condition.stockProductPlusNum,jdbcType=DECIMAL}


  AND STOCK_PRODUCT_PLUS_POSITION = #{condition.stockProductPlusPosition,jdbcType=VARCHAR}


  AND STOCK_PRODUCT_FORMAT_NUM = #{condition.stockProductFormatNum,jdbcType=DECIMAL}


  AND STOCK_PRODUCT_GUARANTEE_TIME = #{condition.stockProductGuaranteeTime,jdbcType=VARCHAR}


  AND STOCK_PRODUCT_SINGLE_NETWEIGHT = #{condition.stockProductSingleNetweight,jdbcType=DECIMAL}


  AND STOCK_PRODUCT_SINGLE_CAPACITY = #{condition.stockProductSingleCapacity,jdbcType=DECIMAL}


  AND STOCK_PRODUCT_BATCH = #{condition.stockProductBatch,jdbcType=VARCHAR}


  AND STOCK_PRODUCT_STATUS = #{condition.stockProductStatus,jdbcType=VARCHAR}


  AND STOCK_PRODUCT_STORAGE = #{condition.stockProductStorage,jdbcType=VARCHAR}


  AND STOCK_PRODUCT_WASTAGE = #{condition.stockProductWastage,jdbcType=DECIMAL}


  AND STOCK_PRODUCT_STORAGE_FEE = #{condition.stockProductStorageFee,jdbcType=DECIMAL}


  AND STOCK_PRODUCT_SUPPLIER_SUBJECT_ID = #{condition.stockProductSupplierSubjectId,jdbcType=VARCHAR}


  AND STOCK_MANUFACTURE_DATE = #{condition.stockManufactureDate,jdbcType=DATE}


  AND STOCK_EXPIRE_DATE = #{condition.stockExpireDate,jdbcType=DATE}


  AND CREATED_BY = #{condition.createdBy,jdbcType=VARCHAR}


  AND CREATED_DATE BETWEEN #{condition.startDate} AND #{condition.endDate}


  AND UPDATE_BY = #{condition.updateBy,jdbcType=VARCHAR}


