mapper.select:
select

from supplier_subject
where 1=1

  AND SUPPLIER_NAME LIKE #{condition.supplierName}"%"


  AND SUPPLIER_TYPE = #{condition.supplierType,jdbcType=VARCHAR}


  AND SUPPLIER_EMAIL = #{condition.supplierEmail,jdbcType=VARCHAR}


  AND SUPPLIER_LOCATION = #{condition.supplierLocation,jdbcType=VARCHAR}


  AND SUPPLIER_CELLPHONE = #{condition.supplierCellphone,jdbcType=VARCHAR}


  AND SUPPLIER_ADDRESS = #{condition.supplierAddress,jdbcType=VARCHAR}


  AND SUPPLIER_REMARK = #{condition.supplierRemark,jdbcType=VARCHAR}


  AND CREATED_BY = #{condition.createdBy,jdbcType=VARCHAR}


  AND CREATED_DATE = #{condition.createdDate,jdbcType=TIMESTAMP}


  AND UPDATE_BY = #{condition.updateBy,jdbcType=VARCHAR}


  AND UPDATE_DATE = #{condition.updateDate,jdbcType=TIMESTAMP}


  AND VERSION = #{condition.version,jdbcType=BIGINT}


  AND STATUS = #{condition.status,jdbcType=VARCHAR}

ORDER BY CREATED_DATE DESC
limit #{startIndex},#{limit}

select
count(1)
from supplier_subject
where 1=1

  AND SUPPLIER_NAME LIKE #{condition.supplierName}"%"


  AND SUPPLIER_TYPE = #{condition.supplierType,jdbcType=VARCHAR}


  AND SUPPLIER_EMAIL = #{condition.supplierEmail,jdbcType=VARCHAR}


  AND SUPPLIER_LOCATION = #{condition.supplierLocation,jdbcType=VARCHAR}


  AND SUPPLIER_CELLPHONE = #{condition.supplierCellphone,jdbcType=VARCHAR}


  AND SUPPLIER_ADDRESS = #{condition.supplierAddress,jdbcType=VARCHAR}


  AND SUPPLIER_REMARK = #{condition.supplierRemark,jdbcType=VARCHAR}


  AND CREATED_BY = #{condition.createdBy,jdbcType=VARCHAR}


  AND CREATED_DATE = #{condition.createdDate,jdbcType=TIMESTAMP}


  AND UPDATE_BY = #{condition.updateBy,jdbcType=VARCHAR}


  AND UPDATE_DATE = #{condition.updateDate,jdbcType=TIMESTAMP}


  AND VERSION = #{condition.version,jdbcType=BIGINT}


  AND STATUS = #{condition.status,jdbcType=VARCHAR}
